-- map variables
--size in tile
back_1_map_w = 315
back_2_map_w = 175
back_3_map_w = 100
map_h = 14
--init x to 0
back_1_map_x = 0
back_1_map_y = 0
back_2_map_x = 0
back_2_map_y = 0
back_3_map_x = 0
back_3_map_y = 0
--load off screen 2 tile for smooth scroling
map_display_buffer = 2
--number of tile to render on display
map_display_w = 30
map_display_h = 14.5
--size of a tile
tile_w = 64
tile_h = 64

--draw the background
function background_1_draw()
  --set offset to match the border
  offset_x = back_1_map_x % tile_w
  offset_y = back_1_map_y % tile_h
  --set the first tile position
  firstTile_x = math.floor(back_1_map_x / tile_w)
  firstTile_y = math.floor(back_1_map_y / tile_h)

  --loop through the double array and display the corresponding tile
  for y=1, (map_display_h + map_display_buffer) do
    for x=1, (map_display_w + map_display_buffer) do
      -- Note that this condition block allows us to go beyond the edge of the map.
      if y+firstTile_y >= 1 and y+firstTile_y <= map_h
       and x+firstTile_x >= 1 and x+firstTile_x <= back_1_map_w
      then
        love.graphics.draw(
          back_tile[back_1_map[y+firstTile_y][x+firstTile_x]], 
          ((x-1)*tile_w) - offset_x - tile_w/2, 
          ((y-1)*tile_h) - offset_y - tile_h/2)
      end
    end
  end
end

function background_2_draw()
  offset_x = back_2_map_x % tile_w
  offset_y = back_2_map_y % tile_h
  firstTile_x = math.floor(back_2_map_x / tile_w)
  firstTile_y = math.floor(back_2_map_y / tile_h)

  for y=1, (map_display_h + map_display_buffer) do
    for x=1, (map_display_w + map_display_buffer) do
      if y+firstTile_y >= 1 and y+firstTile_y <= map_h
       and x+firstTile_x >= 1 and x+firstTile_x <= back_2_map_w
      then
        love.graphics.draw(
          back_tile[back_2_map[y+firstTile_y][x+firstTile_x]], 
          ((x-1)*tile_w) - offset_x - tile_w/2, 
          ((y-1)*tile_h) - offset_y - tile_h/2)
      end
    end
  end
end

function background_3_draw()
  offset_x = back_3_map_x % tile_w
  offset_y = back_3_map_y % tile_h
  firstTile_x = math.floor(back_3_map_x / tile_w)
  firstTile_y = math.floor(back_3_map_y / tile_h)

  for y=1, (map_display_h + map_display_buffer) do
    for x=1, (map_display_w + map_display_buffer) do
      if y+firstTile_y >= 1 and y+firstTile_y <= map_h
       and x+firstTile_x >= 1 and x+firstTile_x <= back_3_map_w
      then
        love.graphics.draw(
          back_tile[back_3_map[y+firstTile_y][x+firstTile_x]], 
          ((x-1)*tile_w) - offset_x - tile_w/2, 
          ((y-1)*tile_h) - offset_y - tile_h/2)
      end
    end
  end
end


